import React from "react";
import "./Kartica.css";

export default function Kartica(props) {
  let { title, image, price } = props.osoba;
  let broj = props.setCartNum;
  let naziv = title;
  if (naziv.length > 30) {
    naziv = naziv.slice(0, 30) + "...";
  }

  let addToChart = (e) => {
    //ovo sam koristio dok nisam nasao na netu da iz parent komponente vucem setState koji mi ne radi
    let idArtikla = e.target.getAttribute("value");
    props.setCartNum(idArtikla);
    //broj(5);
  };

  return (
    <div className="kartica">
      <p className="naslov">{naziv}</p>
      <div className="slikaContainer">
        <img src={image} className="slika" alt="slika" />
      </div>
      <p className="naslov">{price}</p>
      <div
        className="dugme"
        value={props.osoba.id}
        onClick={(e) => addToChart(e.value)}
      >
        ADD TO CART
      </div>
    </div>
  );
}
